mapper.insert:
insert into reservations (
  site_type_id
  , date_from
  , stay_days
  , number_of_people
  , total_amount_tax_incl
  , sales_tax
  , member_id
  , non_member_name
  , non_member_mail
  , non_member_phone_number
) values (
  #{siteTypeId}
  , #{dateFrom}
  , #{stayDays}
  , #{numberOfPeople}
  , #{totalAmountTaxIncl}
  , #{salesTax}
  , #{memberId}
  , #{nonMemberName}
  , #{nonMemberMail}
  , #{nonMemberPhoneNumber}
)
